import Dropdown from './dropdown';
import { useState } from 'react'

function Content() {

const [dropDown, setDropDown] = useState(false)

    const adddropdown = (e) => {
        console.log(e)
        // let rect = e.target.
                // console.log(rect)
            return ( 
        <div style={{color: "red"}} id = 'dropdown-selector'>
            <select>
                <option value={'Wally'}>Wally</option>
                <option value={'Wizard'}>Wizard</option>
                <option value={'Odlaw'}>Odlaw</option>
            </select>
        </div>
        )
}

const callingStatement = (event) => {
    setDropDown(true)
    adddropdown(event)
}


    return (
        <div className="content-wrapper">
            {dropDown && adddropdown()} 
            {/* ^^ if the state and dropdown are equal to true render dropdown. As the Dropdown is always true 
            we just need to adjust the dropDown to true on a click like the one in the img below. 
            IE The expression is just if first part is true, then this is equal to the second part*/}
            <img onClick={(e) => {callingStatement(e)}} id = 'wally-level-1-image' src={wallyLevel1}></img>
        </div>
    )
}   
export default Content;
